+    display: flex;
+    justify-content: center;
+    font-size: 12px;
+    width: 13%;
+}
+
+.datenEingabe .dauer input {
+    box-sizing: border-box;
+    width: 90%;
+    text-align: center;
+    border-radius: 5px;
+    border: 1px solid rgb(193, 193, 193);
+}
+
