+    box-sizing: border-box;
+    display: flex;
+    justify-content: center;
+    width: 40%;
+}
+
+.datenEingabe .was select {
+    box-sizing: border-box;
+    width: 95%;
+    font-size: 12px;
+    border-radius: 5px;
+    border: 1px solid rgb(193, 193, 193);
+}
+
+.datenEingabe .mod {
+    box-sizing: border-box;
+    display: flex;
+    justify-content: center;
+    width: 10%;
+}
+
+.datenEingabe .mod button {
+    box-sizing: border-box;
+    width: 60%;
+    font-size: 12px;
+    border-radius: 5px;
+    border: 1px solid rgb(193, 193, 193);
+}
+
+table {
+    width: 100%;
+    border-collapse: collapse;
+}
+
