+}
+
+table td,
+table th {
+    border: 1px solid #ddd;
+}
+
+table th {
+    text-align: left;
+}
+
+table td {
+    width: auto;
+}
+
+table th.datum,
+table td.datum {
+    text-align: right;
+    padding: 8px 8px 8px 0;
+}
+
+table th.datum button {
+    border-radius: 5px;
+    border: 1px solid rgb(193, 193, 193);
+}
+
+table th.dauer,
+table td.dauer {
+    text-align: right;
+    padding: 8px 8px 8px 0;
+}
+
+table th.kg_min,
+table td.kg_min {
+    text-align: right;
+    padding: 8px 8px 8px 0;
+}
+
+.datenEingabe input {
+    width: 40%;
+}
+
+.datenEingabe select {
+    width: 40%;
+}
+
+.datenEingabe button {
+    width: 10%;
+}
+
+table th.was,
+table td.was {
+    text-align: left;
+    padding: 8px 0 8px 8px;
+}
+
