commits.content:
main {
    margin: 0 auto;
    max-width: 600px;
}

h1 {
    width: 100%;
    text-align: center;
    margin: 1em 0 0.5em 0;
}

nav {
    width: 100%;
    display: flex;
    margin: 0 0 1em 0;
    justify-content: center;
}

nav .navButtons {
    display: flex;
    align-items: center;
    margin: 0.5em 0;
}

.arrow-button {
    background-color: #e3e4e6;
    color: black;
    border: none;
    padding: 5px;
    cursor: pointer;
    display: inline-flex;
    align-items: center;
    border-radius: 5px;
}

.arrow-button.prev:before {
    content: '';
    display: block;
    width: 0;
    height: 0;
    border-right: 6px solid #fff;
    border-top: 6px solid transparent;
    border-bottom: 6px solid transparent;
    margin: 0 5px;
    color: black;
}

.arrow-button.next:before {
    content: '';
    display: block;
    width: 0;
    height: 0;
    border-left: 6px solid #fff;
    border-top: 6px solid transparent;
    border-bottom: 6px solid transparent;
    margin: 0 5px;
}

.arrow-button:hover {
    background-color: #c3c4c5;
    color: black;
}

nav .navButtons .wrapperToday {
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 0 1em;
}

.datenEingabeWrapper {}

.datenEingabeWrapper h2 {
    font-size: 1.2rem;
    margin: 1em;
    text-align: center;
}

.datenEingabe {
    width: 100%;
    display: flex;
    justify-content: space-around;
    margin-bottom: 0.5em;
}

.datenEingabe .datum {
    box-sizing: border-box;
    width: 19.5%;
    display: flex;
    justify-content: center;
}

.datenEingabe .datum input[type='date'] {
    box-sizing: border-box;
    font-size: 11px;
    width: 90%;
    border-radius: 5px;
    border: 1px solid rgb(193, 193, 193);
}

.datenEingabe .dauer {
    box-sizing: border-box;
    display: flex;
    justify-content: center;
    font-size: 12px;
    width: 13%;
}

.datenEingabe .dauer input {
    box-sizing: border-box;
    width: 90%;
    text-align: center;
    border-radius: 5px;
    border: 1px solid rgb(193, 193, 193);
}

.datenEingabe .kg_min {
    box-sizing: border-box;
    display: flex;
    justify-content: center;
    width: 17%;
}

.datenEingabe .kg_min input {
    box-sizing: border-box;
    width: 90%;
    font-size: 12px;
    border-radius: 5px;
    border: 1px solid rgb(193, 193, 193);
}

.datenEingabe .was {
    box-sizing: border-box;
    display: flex;
    justify-content: center;
    width: 40%;
}

.datenEingabe .was select {
    box-sizing: border-box;
    width: 95%;
    font-size: 12px;
    border-radius: 5px;
